<style type="text/css">

	table#web-logger {
		font-family: "Courier New", Courier, monospace;
		font-size: small;
		border-collapse: collapse;
	}

	table#web-logger tr {
		padding: 1px;
		border-top: 1px solid #ccc;
	
	}

	table#web-logger tr.success {
		border-color: darkgreen;
		color: darkgreen;
		background: lightgreen;
	}

	table#web-logger tr.warn {
		border-color: darkorange;
		color: darkorange;
		background: lightyellow;
	}

	table#web-logger tr.error {
		border-color: darkred;
		color: darkred;
		background: lightcoral;
	}

	table#web-logger tr.info {
		border-color: lightgray;
		color: black;
		background: white;
	}


	table#web-logger td {
		padding:8px;
		
	}
</style>